class ArrayStack():
    """Âü∫‰∫éÊï∞ÁªÑÂÆûÁé∞ÁöÑÈ°∫Â∫èÊ†à"""
    def __init__(self, n: int):
        self.items = [None] * n  # Êï∞ÁªÑ
        self.count = 0  # Ê†à‰∏≠ÂÖÉÁ¥†ÁöÑ‰∏™Êï∞
        self.n = n  # Ê†àÁöÑÂ§ßÂ∞è
    def push(self, item) -> bool:
        # Êï∞ÁªÑÁ©∫Èó¥‰∏çÂ§ü‰∫ÜÔºåÁõ¥Êé•ËøîÂõû falseÔºåÂÖ•Ê†àÂ§±Ë¥•„ÄÇ
        if self.count == self.n:
            return False
        # Â∞ÜitemÊîæÂà∞‰∏ãÊ†á‰∏∫countÁöÑ‰ΩçÁΩÆÔºåÂπ∂‰∏îcountÂä†1
        self.items[self.count] = item
        self.count += 1
        return True
    def pop(self):
        # Ê†à‰∏∫Á©∫ÔºåÂàôÁõ¥Êé•ËøîÂõû None
        if self.count == 0:
            return None
        # ËøîÂõû‰∏ãÊ†á‰∏∫count-1ÁöÑÊï∞ÁªÑÂÖÉÁ¥†ÔºåÂπ∂‰∏îÊ†à‰∏≠ÂÖÉÁ¥†‰∏™Êï∞countÂáè1
        tmp = self.items[self.count - 1]
        self.count -= 1
        return tmp
    def __str__(self):
        return str(self.items[:self.count])
